    //         // clear the value before loading 
    //         setMovie({});
    //         setIsError(false);
    //         const {data} = await fetchData(form);

    //         if(data.Response === "True"){

    //         // console.log(response.data);
    //         setMovie(data);
    //         }else{
    //             setIsError(true);
    //         }
    //     }


    const getMovieRequest = async () => {
        const url = `http://www.omdbapi.com/?s=${search}&apikey=f2b88761`;
        const response = await fetch(url);
        const responseJson = await response.json();   
        if (responseJson.Search)  {
                setMovie(responseJson.Search);
            }

        console.log(movie);

      };



        useEffect(() => {
            getMovieRequest(search);
        }, [search]);








  return (
    <div className='bg-dark py-5 rounded p-2'>
    <Form onSubmit={getMovieRequest}>


      <Row className='g-2'>

        <Col className='d-flex justify-content-center'>

          <div className='d-flex' style={{width:"60%"}}>
          <Form.Control 
          placeholder="movie  name"
          onChange={handleOnChange}
          required
        //   value = {form}
          />
          </div>
        </Col>
        <div className="d-grid" style={{width:"40%"}}>
         <Button variant = "primary" type='submit' >search</Button>
         </div>

      </Row>
    </Form>
    <div className="mt-3 d-flex justify-content-center">
        {" " }

        {/* pass it to the custom card  */}

        {movie.imdbID && <CustomCard movie ={movie} func={handleOnAddToList} remov={removeMovie}/>}
